// Parse trend analysis and select best idea
const trendResponse = JSON.parse($node["Trend Analysis"].json.body);
const trendAnalysis = trendResponse.choices[0].message.content;
const channelData = $node["Initialize Workflow"].json.channelData;
const currentTimestamp = $node["Initialize Workflow"].json.userData.timestamp;

// Parse content ideas
const contentIdeas = [];
const ideaBlocks = trendAnalysis.split(/\d+\)/).slice(1);

ideaBlocks.forEach((block) => {
  const lines = block.trim().split('\n');
  const title = lines[0].replace(/^[\s\-A-Za-z]*:\s*/, '');

  let category = '', trending = '', keyPoints = [], searchVolume = '', keywords = [];
  let subNiche = '';

  lines.forEach(line => {
    if (line.toLowerCase().includes('category') || line.toLowerCase().includes('niche') || line.toLowerCase().includes('sub-niche')) {
      category = line.split(':')[1]?.trim() || '';
      // Determine which sub-niche this falls under
      for (const niche of channelData.subNiches) {
        if (category.toLowerCase().includes(niche.toLowerCase())) {
          subNiche = niche;
          break;
        }
      }
      // Default to main niche if no sub-niche is found
      if (!subNiche) subNiche = channelData.mainNiche;
    } else if (line.toLowerCase().includes('trending') || line.toLowerCase().includes('why')) {
      trending = line.split(':')[1]?.trim() || '';
    } else if (line.toLowerCase().includes('key points') || line.toLowerCase().includes('cover')) {
      const pointsText = line.split(':')[1]?.trim() || '';
      keyPoints = pointsText.split(',').map(p => p.trim());
    } else if (line.toLowerCase().includes('search volume') || line.toLowerCase().includes('interest')) {
      searchVolume = line.split(':')[1]?.trim() || '';
    } else if (line.toLowerCase().includes('keyword')) {
      const keywordsText = line.split(':')[1]?.trim() || '';
      keywords = keywordsText.split(',').map(k => k.trim());
    }
  });

  contentIdeas.push({
    title, 
    category, 
    subNiche,
    trending, 
    keyPoints, 
    searchVolume, 
    keywords
  });
});

// Add core niche keywords to all ideas
const coreNicheKeywords = [
  "AI productivity", 
  "AI tools", 
  "productivity automation",
  "AI workflow",
  channelData.mainNiche
];

contentIdeas.forEach(idea => {
  idea.keywords = [...new Set([...idea.keywords, ...coreNicheKeywords])];
});

// Select the best idea based on search volume
let selectedIdea = contentIdeas[0];
for (const idea of contentIdeas) {
  if (idea.searchVolume && idea.searchVolume.toLowerCase().includes('high')) {
    selectedIdea = idea;
    break;
  }
}

