// Error handling function to check if previous node executed successfully
function checkNodeSuccess(nodeName, expectedProperties = []) {
  try {
    const nodeData = $node[nodeName];

    if (!nodeData || !nodeData.json) {
      return {
        success: false,
        message: `Node "${nodeName}" did not return any data`
      };
    }

    // Special handling for YouTube API responses that come in items array
    if (nodeName === "Get YouTube Channel") {
      if (!nodeData.json.items || !nodeData.json.items.length) {
        return {
          success: false,
          message: `Node "${nodeName}" did not return any channel items`
        };
      }

      // Check expected properties in the first item
      for (const prop of expectedProperties) {
        if (nodeData.json.items[0][prop] === undefined) {
          return {
            success: false,
            message: `Node "${nodeName}" is missing expected property "${prop}" in channel data`
          };
        }
      }

      return {
        success: true,
        data: nodeData.json.items[0]
      };
    }

    // Regular property checks for other nodes
    for (const prop of expectedProperties) {
      if (nodeData.json[prop] === undefined) {
        return {
          success: false,
          message: `Node "${nodeName}" is missing expected property "${prop}"`
        };
      }
    }

    return {
      success: true,
      data: nodeData.json
    };
  } catch (error) {
    return {
      success: false,
      message: `Error processing ${nodeName}: ${error.message}`
    };
  }
}

// Check channel data (uses items array from YouTube API)
const channelCheck = checkNodeSuccess("Get YouTube Channel", ["id", "snippet", "statistics"]);
if (!channelCheck.success) {
  return {
    error: true,
    message: channelCheck.message,
    stage: "channel_data",
    continue_workflow: false
  };
}

// Check YouTube Analytics
const analyticsCheck = checkNodeSuccess("YouTube Analytics", ["rows"]);
if (!analyticsCheck.success) {
  return {
    error: true,
    message: analyticsCheck.message,
    stage: "analytics_data",
    continue_workflow: false
  };
}

// Check trending topics
const trendingCheck = checkNodeSuccess("Get Trending Topics");
if (!trendingCheck.success) {
  // Not critical, we can continue without it
  console.log("Warning: " + trendingCheck.message);
}

// Process all inputs for content analysis
