// Split the videos array from the AI response
const responseText = $input.first().json.response;

let videoData;
try {
  // Parse the JSON response from OpenAI
  videoData = JSON.parse(responseText);

  // If it's not an array but a single object, wrap it in an array
  if (!Array.isArray(videoData)) {
    videoData = [videoData];
  }
} catch (error) {
  // If there's an error parsing JSON, try to extract JSON objects
  const jsonPattern = /\{([^{}]|\{[^{}]*\})*\}/g;
  const matches = responseText.match(jsonPattern);

  if (matches && matches.length > 0) {
    videoData = matches.map(match => JSON.parse(match));
  } else {
    throw new Error('Could not parse video data from response');
  }
}

// Return the first video concept to process
return videoData.map(video => ({ json: video }));

parameters.functionCode:
// Process a Pexels video response to get download URLs
const videos = $input.first().json.videos;
const videoUrls = [];

// Get 5 random videos from results for variety
const selectedVideos = [];
for (let i = 0; i < Math.min(5, videos.length); i++) {
  const randomIndex = Math.floor(Math.random() * videos.length);
  if (!selectedVideos.includes(randomIndex)) {
    selectedVideos.push(randomIndex);
  }
}

// Get highest quality video file URL for each selected video
selectedVideos.forEach(index => {
  const video = videos[index];
  const videoFile = video.video_files.reduce((prev, current) => {
    return (prev.quality === 'hd' || prev.height > current.height) ? prev : current;
  });
  videoUrls.push(videoFile.link);
});

// Return with the first URL to download
return [{
  json: {
    videoUrls: videoUrls,
    downloadUrl: videoUrls[0]
  }
}];
