const twoWeeksAgoPrice = stockData.length > 10 ? stockData[0].c : currentPrice;
const priceChange = currentPrice - twoWeeksAgoPrice;
const priceChangePercent = (priceChange / twoWeeksAgoPrice * 100).toFixed(2);

// Process fundamental data
