const companyName = companyData.name || data.currentStock;
const logoUrl = `https://logo.clearbit.com/${companyData.homepage_url?.replace('https://', '').replace('http://', '').split('/')[0]}`;

// Determine market trend
const marketTrend = priceChange >= 0 ? 'bullish' : 'bearish';

// Create custom recommendation based on multiple factors
let stockRecommendation = 'HOLD';
if (priceChangePercent > 5) stockRecommendation = 'STRONG BUY';
else if (priceChangePercent > 0) stockRecommendation = 'BUY';
else if (priceChangePercent < -5) stockRecommendation = 'SELL';
else if (priceChangePercent < 0) stockRecommendation = 'HOLD';

// Calculate key support and resistance levels
const pricePoints = stockData.map(item => item.c);
const maxPrice = Math.max(...pricePoints);
const minPrice = Math.min(...pricePoints);
const resistanceLevel = (maxPrice * 1.02).toFixed(2); // 2% above max
const supportLevel = (minPrice * 0.98).toFixed(2); // 2% below min

// Calculate RSI (simple implementation)
let rsi = 50; // Default neutral
if (pricePoints.length > 5) {
  let gains = 0, losses = 0;
  for (let i = 1; i < pricePoints.length; i++) {
    const change = pricePoints[i] - pricePoints[i-1];
    if (change > 0) gains += change;
    else losses -= change; // Make positive for calculation
  }

  if (gains + losses > 0) {
    const relativeStrength = gains / (losses || 1); // Avoid division by 0
    rsi = 100 - (100 / (1 + relativeStrength));
  }
}

