// Process weekly video data into digital product
const videos = $input.all().map(i => i.json);

// Group by content type
const contentTypes = {
  market_outlook: [],
  technical_analysis: [],
  fundamentals_deep_dive: [],
  options_strategy: [],
  sector_analysis: [],
  dividend_analysis: [],
  investing_basics: []
};

videos.forEach(video => {
  const contentType = video['Content Type'] || 'market_outlook';
  if (contentTypes[contentType]) {
    contentTypes[contentType].push(video);
  }
});

// Get top picks
const allStocks = {};
videos.forEach(video => {
  const stock = video['Stock'];
  if (!allStocks[stock]) {
    allStocks[stock] = {
      ticker: stock,
      count: 0,
      lastPrice: parseFloat(video['Stock Price']) || 0,
      recommendation: video['Recommendation'] || 'HOLD',
      rsi: parseFloat(video['RSI']) || 50,
      supportLevel: parseFloat(video['Support Level']) || 0,
      resistanceLevel: parseFloat(video['Resistance Level']) || 0,
      videoUrl: video['YouTube URL'] || ''
    };
  }
  allStocks[stock].count++;
});

// Score stocks
const scoredStocks = Object.values(allStocks).map(stock => {
  const recommendationScore = 
    stock.recommendation === 'STRONG BUY' ? 5 :
    stock.recommendation === 'BUY' ? 4 :
    stock.recommendation === 'HOLD' ? 3 :
    stock.recommendation === 'SELL' ? 2 : 1;

  const rsiScore = 
    stock.rsi > 70 ? 1 :
    stock.rsi < 30 ? 5 :
    stock.rsi > 60 ? 2 :
    stock.rsi < 40 ? 4 : 3;

  const totalScore = recommendationScore * 0.6 + rsiScore * 0.4;

  return {
    ...stock,
    score: totalScore
  };
});

// Sort by score
scoredStocks.sort((a, b) => b.score - a.score);

// Format current date for reporting
const reportDate = new Date().toLocaleDateString('en-US', {
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

return {
  reportTitle: `Market Mastery Weekly Report - ${reportDate}`,
  reportDate: reportDate,
  topPicks: scoredStocks.slice(0, 5),
  contentTypeBreakdown: contentTypes,
  videosAnalyzed: videos.length,
  userName: 'onabellaabdullaziz',
  channelName: 'Market Mastery Daily',
  timestamp: '2025-05-17 21:51:51',
  productType: 'weekly_report',
  productId: `mmwr_${Date.now()}`,
  price: 9.99
};
