const lastPrice = prices[prices.length - 1];
const previousPrice = prices[prices.length - 2];
const priceChange = lastPrice - previousPrice;
const priceChangePercent = (priceChange / previousPrice * 100).toFixed(2);

// Calculate support and resistance
const min = Math.min(...prices);
const max = Math.max(...prices);
const range = max - min;
const supportLevel = (lastPrice - (range * 0.3)).toFixed(2);
const resistanceLevel = (lastPrice + (range * 0.3)).toFixed(2);

// Calculate RSI
function calculateRSI(prices, period = 14) {
  const changes = [];
  for (let i = 1; i < prices.length; i++) {
    changes.push(prices[i] - prices[i - 1]);
  }

  const gains = changes.map(change => change > 0 ? change : 0);
  const losses = changes.map(change => change < 0 ? Math.abs(change) : 0);

  let avgGain = gains.slice(0, period).reduce((sum, gain) => sum + gain, 0) / period;
  let avgLoss = losses.slice(0, period).reduce((sum, loss) => sum + loss, 0) / period;

  for (let i = period; i < changes.length; i++) {
    avgGain = (avgGain * (period - 1) + gains[i]) / period;
    avgLoss = (avgLoss * (period - 1) + losses[i]) / period;
  }

  const rs = avgGain / avgLoss;
  return 100 - (100 / (1 + rs));
}

const rsi = calculateRSI(prices).toFixed(2);

// Calculate MACD (new in v2.1)
function calculateMACD(prices, fastPeriod = 12, slowPeriod = 26, signalPeriod = 9) {
  // Calculate EMAs
  function calculateEMA(prices, period) {
    const k = 2 / (period + 1);
    let ema = prices[0];
    const emaResults = [ema];

    for (let i = 1; i < prices.length; i++) {
      ema = (prices[i] * k) + (ema * (1 - k));
      emaResults.push(ema);
    }

    return emaResults;
  }

  const fastEMA = calculateEMA(prices, fastPeriod);
  const slowEMA = calculateEMA(prices, slowPeriod);

  // Calculate MACD line
  const macdLine = [];
  for (let i = 0; i < prices.length; i++) {
    if (i >= slowPeriod - 1) {
      macdLine.push(fastEMA[i] - slowEMA[i]);
    } else {
      macdLine.push(0);
    }
  }

  // Calculate signal line
  const signalLine = calculateEMA(macdLine.slice(slowPeriod - 1), signalPeriod);

  // Calculate histogram
  const histogram = [];
  for (let i = 0; i < signalLine.length; i++) {
    histogram.push(macdLine[i + slowPeriod - 1] - signalLine[i]);
  }

  return {
    macdLine: macdLine[macdLine.length - 1].toFixed(2),
    signalLine: signalLine[signalLine.length - 1].toFixed(2),
    histogram: histogram[histogram.length - 1].toFixed(2),
    trend: histogram[histogram.length - 1] > 0 ? "bullish" : "bearish"
  };
}

const macd = calculateMACD(prices);

// Generate stock recommendation
