  if (opportunity.audience_segment_receptivity && advancedFeatures.advancedAudienIntelligence) {
    finalScore *= (1 + (opportunity.audience_segment_receptivity * 0.12));
  }

  // Cross-platform synergy
  if (opportunity.platform_synergy_score && channelConfig.crossPlatformStrategy.crossPlatformSynergy === "high") {
    finalScore *= (1 + (opportunity.platform_synergy_score * 0.1));
  }

  // Cap at 100 and round
  return Math.min(Math.round(finalScore), 100);
}

