const seoFunctions = {
  // Generate optimal YouTube title based on keyword research
  generateOptimizedTitle: (primaryKeyword, contentType, year) => {
    const titleFormulas = seoSystem.youtubeSearchOptimization.metadataTemplates.titleFormulas;

    // Find most appropriate title formula for this content type
    let bestFormula = titleFormulas.find(formula => formula.bestFor === contentType);
    if (!bestFormula) {
      bestFormula = titleFormulas[0]; // Default to first formula if no match
    }

    // Apply the formula
    let title = bestFormula.pattern
      .replace('{{KEYWORD}}', primaryKeyword)
      .replace('{{YEAR}}', year || new Date().getFullYear())
      .replace('{{NUMBER}}', Math.floor(Math.random() * 5) + 5) // Random number between 5-10
      .replace('{{ADJECTIVE}}', 'Changing Everything')
      .replace('{{TIME_PERIOD}}', '30 Days');

    // Ensure title is not too long (YouTube limit is around 100 chars, but 60 is better for display)
    if (title.length > 60) {
      title = title.substring(0, 57) + '...';
    }

    return title;
  },

  // Generate optimized description with all necessary SEO elements
  generateOptimizedDescription: (primaryKeyword, secondaryKeywords, contentDetails, resources) => {
    const templates = seoSystem.youtubeSearchOptimization.metadataTemplates.descriptionTemplates;

    // Create intro section
    const intro = templates.find(t => t.section === 'intro').pattern
      .replace('{{primary_keyword_rich_summary}}', `Looking for the best way to master ${primaryKeyword}? You're in the right place.`)
      .replace('{{value_proposition}}', `exactly how to use ${primaryKeyword} effectively`)
      .replace('{{desired_outcome}}', `implement ${primaryKeyword} in your own projects`);

    // Create timestamps (simplified - would be dynamic in production)
    const timestamps = templates.find(t => t.section === 'timestamps').pattern
      .replace('{{auto_generated_timestamps}}', '01:15 Understanding the Basics\n03:42 Essential Techniques\n07:18 Advanced Strategies\n12:05 Common Mistakes to Avoid')
      .replace('{{final_timestamp}}', '15:30');

    // Create content details section
    const details = templates.find(t => t.section === 'content_details').pattern
      .replace('{{key_point_1}}', `The fundamentals of ${primaryKeyword}`)
      .replace('{{key_point_2}}', `How to set up ${primaryKeyword} correctly`)
      .replace('{{key_point_3}}', `Advanced ${primaryKeyword} techniques`)
      .replace('{{key_point_4}}', `Troubleshooting common ${primaryKeyword} issues`)
      .replace('{{key_point_5}}', `${primaryKeyword} optimization tips`);

    // Create resources section
    const resourceSection = templates.find(t => t.section === 'resources').pattern
      .replace('{{resource_1}}', `${primaryKeyword} Cheat Sheet`)
      .replace('{{resource_1_link}}', 'https://example.com/resources')
      .replace('{{resource_2}}', `${secondaryKeywords[0]} Guide`)
      .replace('{{resource_2_link}}', 'https://example.com/guide')
      .replace('{{resource_3}}', `${secondaryKeywords[1]} Tutorial`)
      .replace('{{resource_3_link}}', 'https://example.com/tutorial');

    // Create call to action
    const cta = templates.find(t => t.section === 'call_to_action').pattern
      .replace('{{channel_topic}}', primaryKeyword);

    // Create social proof section
    const social = templates.find(t => t.section === 'social_proof').pattern
      .replace('{{website_url}}', 'https://example.com')
      .replace('{{instagram_handle}}', '@channelname')
      .replace('{{twitter_handle}}', '@channelname');

    // Create keyword section (hidden in UI but visible to algorithm)
    const keywordSection = templates.find(t => t.section === 'keyword_section').pattern
      .replace('{{primary_keyword}}', primaryKeyword)
      .replace('{{secondary_keyword_1}}', secondaryKeywords[0])
      .replace('{{secondary_keyword_2}}', secondaryKeywords[1])
      .replace('{{secondary_keyword_3}}', secondaryKeywords[2] || secondaryKeywords[0]);

    // Combine all sections in optimal order
    return `${intro}\n\n${timestamps}\n\n${details}\n\n${resourceSection}\n\n${cta}\n\n${social}\n\n${keywordSection}`;
  },

  // Generate optimized tags based on keyword research
  generateOptimizedTags: (primaryKeyword, secondaryKeywords, contentType) => {
    const tagStrategy = seoSystem.youtubeSearchOptimization.metadataTemplates.tagStrategies;

    // Create primary tags (exact match and slight variations)
    const primaryTags = [
      primaryKeyword,
      `best ${primaryKeyword}`,
      `${primaryKeyword} tutorial`,
      `${primaryKeyword} guide`,
      `how to ${primaryKeyword}`
    ];

    // Create secondary tags (semantic variations)
    const secondaryTags = secondaryKeywords.map(keyword => keyword);

    // Create longtail tags (question-based)
    const longtailTags = [
      `how to use ${primaryKeyword}`,
      `why is ${primaryKeyword} important`,
      `${primaryKeyword} for beginners`,
      `${primaryKeyword} advanced techniques`,
      `${primaryKeyword} tips and tricks`
    ];

    // Create brand and category tags
    const brandTags = ['channel name', 'series name'];
    const categoryTags = [contentType, 'learning', 'education', 'tutorial'];

    // Combine all tags and limit to YouTube's maximum (500 characters total)
    const allTags = [...primaryTags, ...secondaryTags, ...longtailTags, ...brandTags, ...categoryTags];

    // Filter duplicates and limit length
    const uniqueTags = [...new Set(allTags)];
    let tagString = '';
    const finalTags = [];

    for (const tag of uniqueTags) {
      if (tagString.length + tag.length + 1 < 500) { // +1 for the comma
        tagString += tag + ',';
        finalTags.push(tag);
      } else {
        break;
      }
    }

    return finalTags;
  },

  // Analyze content for SEO effectiveness
  analyzeContentSEO: (title, description, tags, script) => {
    // Create scoring system (simplified)
    const scores = {
      title: {
        length: title.length <= 60 ? 100 : 60 / title.length * 100,
        keywordPresence: 100, // Simplified - would check for actual keyword presence
        clickability: title.includes('How') || title.includes('?') || /\d+/.test(title) ? 100 : 70,
        overall: 0
      },
      description: {
        length: description.length >= 250 ? 100 : description.length / 250 * 100,
        timestampsPresent: description.includes('00:00') ? 100 : 0,
        keywordDensity: 100, // Simplified
        overall: 0
      },
      tags: {
        count: tags.length >= 10 ? 100 : tags.length / 10 * 100,
        relevance: 100, // Simplified
        overall: 0
      },
      script: {
        keywordPresence: 100, // Simplified
        engagement: 100, // Simplified
        clarity: 100, // Simplified
        overall: 0
      }
    };

    // Calculate overall scores
    scores.title.overall = (scores.title.length + scores.title.keywordPresence + scores.title.clickability) / 3;
    scores.description.overall = (scores.description.length + scores.description.timestampsPresent + scores.description.keywordDensity) / 3;
    scores.tags.overall = (scores.tags.count + scores.tags.relevance) / 2;
    scores.script.overall = (scores.script.keywordPresence + scores.script.engagement + scores.script.clarity) / 3;

    const overallScore = (scores.title.overall * 0.3) + 
                         (scores.description.overall * 0.3) + 
                         (scores.tags.overall * 0.2) + 
                         (scores.script.overall * 0.2);

    // Generate recommendations
    const recommendations = [];

    if (scores.title.overall < 90) {
      recommendations.push('Improve title by ensuring keyword placement and keeping length under 60 characters');
    }

    if (scores.description.overall < 90) {
      recommendations.push('Enhance description with timestamps, keywords, and a length of at least 250 characters');
    }

    if (scores.tags.overall < 90) {
      recommendations.push('Add more relevant tags, aiming for at least 10 strong keyword phrases');
    }

    if (scores.script.overall < 90) {
      recommendations.push('Strengthen script with better keyword placement and engagement elements');
    }

    return {
      scores,
      overallScore,
      recommendations,
      rating: overallScore >= 90 ? 'Excellent' : 
             overallScore >= 80 ? 'Good' : 
             overallScore >= 70 ? 'Average' : 
             'Needs Improvement'
    };
  },

  // Generate schema markup for video
  generateVideoSchema: (videoTitle, videoDescription, thumbnailUrl, uploadDate, duration, videoUrl) => {
