// Format current date for the metadata
const currentDate = "2025-05-17 13:15:05";
const username = "onabellaabdullaziz";

// Get data from various nodes
const scriptData = $node["Script Integration"].json || {};
const voiceData = $node["ElevenLabs Voice Synthesis"].binary || {};
const videoData = $node["StabilityAI Video Generation"].json || {};
const thumbnailData = $node["Midjourney Thumbnail Generation"].json || {};
const seoData = $node["Mixtral SEO Generation"].json || {};

// Extract voice track binary data or URL
let voiceTrack = { data: null, url: null };
if (voiceData.data) {
  voiceTrack.data = voiceData.data;
} else if (voiceData.url) {
  voiceTrack.url = voiceData.url;
}

// Extract generated video segments
let videoSegments = [];
if (videoData.segments) {
  videoSegments = videoData.segments;
} else if (videoData.video_url) {
  videoSegments = [{ url: videoData.video_url }];
} else {
  // Fallback - this would normally come from the API response
  videoSegments = [{ placeholder: true, duration: 8 }];
}

// Parse SEO metadata
let metadata = {};
try {
  // If the response is already a JSON object
  if (typeof seoData === "object" && seoData.title) {
    metadata = seoData;
  }
  // If the response is a string that needs parsing
  else if (seoData.text || seoData.content) {
    const jsonText = seoData.text || seoData.content;
    metadata = JSON.parse(jsonText);
  }
  // Fallback if we couldn't parse the SEO data
  else {
    const topic = $node["Process Strategy Analysis"].json.selected_topic.title;
    const keywords = $node["Process Strategy Analysis"].json.selected_topic.keywords;

    metadata = {
      title: `Ultimate Guide to ${topic} (2025 Edition)`,
      description: `In this comprehensive guide, we explore everything you need to know about ${topic}. Learn expert strategies, tips, and techniques that will transform your approach.\n\nTimestamps:\n0:00 Introduction\n1:30 Main Content\n7:00 Conclusion\n\nKeywords: ${keywords.join(", ")}`,
      tags: keywords,
      chapters: [
        { title: "Introduction", timestamp: "0:00" },
        { title: "Main Content", timestamp: "1:30" },
        { title: "Conclusion", timestamp: "7:00" }
      ]
    };
  }
} catch (error) {
  // Fallback metadata if parsing fails
  const topic = $node["Process Strategy Analysis"].json.selected_topic.title;
  metadata = {
    title: `Guide to ${topic}`,
    description: `A comprehensive video about ${topic}.`,
    tags: $node["Process Strategy Analysis"].json.selected_topic.keywords || [],
    chapters: []
  };
}

// Organize all data for the video assembly
return {
  audio: {
    voice_track: voiceTrack
  },
  visuals: {
    video_segments: videoSegments,
    thumbnail: thumbnailData.images ? thumbnailData.images[0] : null
  },
  script: scriptData.enhanced_script || scriptData.original_script,
  metadata: metadata,
  monetization: scriptData.monetization_elements,
  creation_info: {
    date: currentDate,
    creator: username
  }
};

