// Process content strategy
const now = new Date();
const today = `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}-${String(now.getDate()).padStart(2, '0')}`;

try {
  // Parse content strategy JSON
  const data = JSON.parse($input.item.json.Body);

  // Find today's content
  const content = data.find(item => item.publishDate === today);
  if (!content) {
    return { 
      noContent: true, 
      message: 'No content scheduled for today',
      date: today,
      timestamp: new Date().toISOString(),
      nextScheduled: data
        .filter(item => new Date(item.publishDate) > now)
        .sort((a, b) => new Date(a.publishDate) - new Date(b.publishDate))[0]
    };
  }

  // Format chapters if available
  const chapters = content.chapters || [];
  const formattedChapters = chapters.map(chapter => {
    return {
      title: chapter.title,
      duration: chapter.duration,
      keyPoints: chapter.keyPoints || [],
      formattedPoints: (chapter.keyPoints || []).join(', ')
    };
  });

  // Calculate total duration from chapters or use target
  const calculatedDuration = chapters.reduce((sum, chapter) => sum + chapter.duration, 0);
  const duration = calculatedDuration > 0 ? calculatedDuration : (content.targetDuration || 10);

  // Format keywords
  const keywords = content.keywords.split(',').map(k => k.trim());

  // Return processed content
  return {
    title: content.title,
    topic: content.topic,
    keywords: content.keywords,
    keywordsArray: keywords,
    duration: duration,
    targetDuration: content.targetDuration || duration,
    noContent: false,
    accessLevel: content.accessLevel || 'free',
    chapters: formattedChapters,
    chaptersForPrompt: formattedChapters.map(c => `${c.title} (${c.duration} min): ${c.formattedPoints}`).join('\n'),
    monetizationProducts: content.monetizationProducts || [],
    contentType: content.contentType || 'educational',
    thumbnailPrompt: content.thumbnailPrompt || `YouTube thumbnail for "${content.title}", professional, high quality`,
    creator: 'onabellaabdullaziz',
    created: '2025-05-22 09:29:17',
    videoId: `video-${Date.now()}`
  };
} catch (error) {
  return {
    noContent: true,
    error: true,
    message: `Error processing content strategy: ${error.message}`,
    timestamp: new Date().toISOString()
  };
}

// Merge content plan with monetization products

const contentPlan = $input.item.json;
const products = JSON.parse($node["Get Monetization Products"].json.Body);

// Find relevant products for this content
const contentType = contentPlan.contentType;
const contentKeywords = contentPlan.keywordsArray;

// Filter products by content type and keywords
const relevantProducts = products.filter(product => {
  // Check if product tags match content keywords
  const hasMatchingTags = product.tags.some(tag => contentKeywords.includes(tag));

  // Check if product is suitable for this content type
  const matchesContentType = product.recommendedFor.includes(contentType);

  return hasMatchingTags || matchesContentType;
}).slice(0, 2); // Limit to top 2 most relevant products

// Add products to content plan
return {
  ...contentPlan,
  monetizationProducts: relevantProducts,
  hasProductRecommendations: relevantProducts.length > 0,
  productRecommendationsText: relevantProducts.map(p => 
    `${p.name} ($${p.price}): ${p.description}`
  ).join('\n\n')
};

// Process prompt template

let template;
try {
  template = JSON.parse($node["Get Content Template"].json.Body);
} catch (error) {
  // Default template if specific one not found
  template = {
    "scriptPrompt": "Create a detailed educational YouTube script about {topic} with the title '{title}'. The video should be {duration} minutes long and cover these main points: {chapters}. Make the content informative, engaging, and optimized for these keywords: {keywords}. Include a strong opening hook, clear explanations, visual cues, and a compelling call to action.",
    "titleOptions": "Generate 5 catchy alternative titles for a YouTube video about {topic}. The current title is '{title}'. Make them engaging and SEO-friendly for these keywords: {keywords}.",
    "descriptionPrompt": "Write an SEO-optimized YouTube description for a video titled '{title}' about {topic}. The video covers: {chapters}. Include relevant keywords: {keywords}. Add timestamps for chapters, relevant tags, and a call to action.",
    "callToAction": "If you found this video helpful, make sure to check out {productName} for more advanced strategies."
  };
}

// Get content data
const content = $node["Prepare Monetization"].json;

// Create prompts by filling in templates
const scriptPrompt = template.scriptPrompt
  .replace("{topic}", content.topic)
  .replace("{title}", content.title)
  .replace("{duration}", content.duration)
  .replace("{chapters}", content.chaptersForPrompt)
  .replace("{keywords}", content.keywords);

const titleOptionsPrompt = template.titleOptions
  .replace("{topic}", content.topic)
  .replace("{title}", content.title)
  .replace("{keywords}", content.keywords);

const descriptionPrompt = template.descriptionPrompt
  .replace("{title}", content.title)
  .replace("{topic}", content.topic)
  .replace("{chapters}", content.chaptersForPrompt)
  .replace("{keywords}", content.keywords);

let callToAction = "";
if (content.hasProductRecommendations && content.monetizationProducts.length > 0) {
  callToAction = template.callToAction.replace("{productName}", content.monetizationProducts[0].name);
}

// Return all prepared prompts
return {
  ...content,
  scriptPrompt: scriptPrompt,
  titleOptionsPrompt: titleOptionsPrompt,
  descriptionPrompt: descriptionPrompt,
  callToAction: callToAction,
  promptsReady: true
};

// Combine all LLM generations

const baseData = $input.item.json;
const script = $node["Generate Script"].json.generated_text;
const description = $node["Generate Description"].json.generated_text;
const titleOptions = $node["Generate Title Options"].json.generated_text;

// Extract alternate titles from generation
const extractTitles = (text) => {
  const titleLines = text.split('\n').filter(line => 
    line.trim().length > 0 && 
    (line.match(/^\d\./) || line.match(/^-/) || line.includes(':'))
  );

  return titleLines.map(line => {
    // Clean up the line to extract just the title
    return line.replace(/^\d+\.\s*/, '')
              .replace(/^-\s*/, '')
              .replace(/^.*?:\s*/, '')
              .trim()
              .replace(/["']/g, '');
  }).filter(title => title.length > 10 && title.length < 100); // Filter for reasonable titles
};

// Process script to extract sections/timestamps
const processScript = (script) => {
  const lines = script.split('\n');
  let sections = [];
  let currentSection = null;
  let currentContent = [];

  // Look for section headers and timestamps
  for (const line of lines) {
    const sectionMatch = line.match(/^#+\s+(.+)$/) || line.match(/^([\w\s]+)[:\-]/) || line.match(/^\d+\.\s+(.+)$/);

    if (sectionMatch && line.length < 100) { // Likely a header
      // Save previous section if exists
      if (currentSection) {
        sections.push({
          title: currentSection,
          content: currentContent.join('\n')
        });
      }

      // Start new section
      currentSection = sectionMatch[1].trim();
      currentContent = [];
    } else if (line.trim()) {
      currentContent.push(line);
    }
  }

  // Add last section
  if (currentSection) {
    sections.push({
      title: currentSection,
      content: currentContent.join('\n')
    });
  }

  return sections;
};

// Generate timestamps based on chapter durations
const generateTimestamps = (chapters) => {
  let currentTime = 0; // in seconds
  return chapters.map(chapter => {
    const startTime = currentTime;
    const startTimeFormatted = formatTime(startTime);

    // Convert chapter duration to seconds and add to current time
    currentTime += chapter.duration * 60;

    return {
      ...chapter,
      startTime: startTime,
      startTimeFormatted: startTimeFormatted
    };
  });
};

// Format time as MM:SS or HH:MM:SS
const formatTime = (seconds) => {
  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  const secs = seconds % 60;

  if (hours > 0) {
    return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  }
  return `${minutes}:${secs.toString().padStart(2, '0')}`;
};

// Process the generated content
const alternativeTitles = extractTitles(titleOptions);
const scriptSections = processScript(script);
const timestampedChapters = generateTimestamps(baseData.chapters);

// Combine everything
return {
  ...baseData,
  script: script,
  description: description,
  alternativeTitles: alternativeTitles,
  scriptSections: scriptSections,
  timestampedChapters: timestampedChapters,
  // Add timestamps to description
  descriptionWithTimestamps: description + '\n\nTimestamps:\n' + 
    timestampedChapters.map(chapter => `${chapter.startTimeFormatted} - ${chapter.title}`).join('\n')
};

