// Process thumbnail image from base64

// Get base64 image data
const baseData = $input.item.json;
const base64Image = $node["Generate Thumbnail"].json.images[0];

// Convert to binary
const binaryData = Buffer.from(base64Image, 'base64');
const filename = `thumbnail-${baseData.videoId}.png`;

return {
  ...baseData,
  binary: {
    thumbnail: {
      data: binaryData,
      mimeType: 'image/png',
      fileName: filename
    }
  },
  thumbnailFilename: filename
};

// Process audio file from base64

// Get base64 audio data
const baseData = $input.item.json;
const audioData = $node["Generate Audio"].json.audio;

// Convert to binary
const binaryData = Buffer.from(audioData, 'base64');
const filename = `voiceover-${baseData.videoId}.wav`;

return {
  ...baseData,
  binary: {
    ...baseData.binary,
    audio: {
      data: binaryData,
      mimeType: 'audio/wav',
      fileName: filename
    }
  },
  audioFilename: filename
};

// Prepare CMS content

const baseData = $input.item.json;
const s3Endpoint = $env.S3_ENDPOINT || 'https://minio-service.railway.app';

// Create HTML content
const createHtml = () => {
  const baseUrl = s3Endpoint.replace(/\/$/, '');
  const thumbnailUrl = `${baseUrl}/youtube-assets/thumbnails/${baseData.thumbnailFilename}`;
  const audioUrl = `${baseUrl}/youtube-assets/audio/${baseData.audioFilename}`;

  // Create sections HTML
  const sectionsHtml = baseData.scriptSections.map(section => {
    return `<h2>${section.title}</h2>\n<p>${section.content.replace(/\n/g, '<br>\n')}</p>`;
  }).join('\n\n');

  // Create timestamps HTML
  const timestampsHtml = baseData.timestampedChapters.map(chapter => {
    return `<li><strong>${chapter.startTimeFormatted}</strong> - ${chapter.title}</li>`;
  }).join('\n');

  // Create product recommendations HTML if available
  let productsHtml = '';
  if (baseData.hasProductRecommendations) {
    productsHtml = `
<h3>Recommended Resources</h3>
<div class="products-container">
${baseData.monetizationProducts.map(product => `
  <div class="product-card">
    <h4>${product.name} - $${product.price}</h4>
    <p>${product.description}</p>
    <a href="/product/${product.id}" class="cta-button">Learn More</a>
  </div>
`).join('')}
</div>`;
  }

  // Construct final HTML
  return `
<div class="content-container">
  <div class="video-container">
    <img src="${thumbnailUrl}" alt="${baseData.title}" class="video-thumbnail">
    <div class="audio-player">
      <audio controls>
        <source src="${audioUrl}" type="audio/wav">
        Your browser does not support the audio element.
      </audio>
    </div>
  </div>

  <div class="content-main">
    ${sectionsHtml}
  </div>

  <div class="timestamps">
    <h3>Timestamps</h3>
    <ul>
      ${timestampsHtml}
    </ul>
  </div>

  ${productsHtml}
</div>

<style>
.content-container { max-width: 800px; margin: 0 auto; }
.video-container { position: relative; margin-bottom: 2em; }
.video-thumbnail { width: 100%; height: auto; }
.audio-player { margin: 1em 0; }
.timestamps { margin: 2em 0; padding: 1em; background: #f7f7f7; border-radius: 5px; }
.products-container { display: flex; gap: 1em; flex-wrap: wrap; margin: 2em 0; }
.product-card { border: 1px solid #ddd; padding: 1em; border-radius: 5px; flex: 1; min-width: 250px; }
.cta-button { display: inline-block; background: #2962ff; color: white; padding: 0.5em 1em; text-decoration: none; border-radius: 3px; margin-top: 1em; }
</style>
`;
};

// Generate tags from keywords and topic
const generateTags = () => {
  const keywords = baseData.keywordsArray;
  const topic = baseData.topic;

  // Start with keywords
  let tags = keywords;

  // Add topic if not already included
  if (!tags.includes(topic)) {
    tags.push(topic);
  }

  // Add a few more related tags
  const contentType = baseData.contentType;
  if (contentType === 'educational') {
    tags.push('tutorial', 'how-to', 'learn');
  } else if (contentType === 'entertainment') {
    tags.push('fun', 'entertainment', 'trending');
  }

  // Return unique tags
  return [...new Set(tags)];
};

return {
  ...baseData,
  htmlContent: createHtml(),
  tags: generateTags(),
  ghostDataReady: true
};

