// Process content strategy
const now = new Date();
const today = `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}-${String(now.getDate()).padStart(2, '0')}`;
// Assuming the content-strategy.json contains scheduled content items
const contentStrategy = JSON.parse($node["Get Content Strategy"].json.Body);

// Find content scheduled for today
const todayContent = contentStrategy.filter(item => item.publishDate === today);

if (todayContent.length === 0) {
  // No content scheduled for today
  // Find the next scheduled content
  const futureContent = contentStrategy
    .filter(item => item.publishDate > today)
    .sort((a, b) => new Date(a.publishDate) - new Date(b.publishDate));

  return {
    noContent: true,
    nextScheduled: futureContent.length > 0 ? futureContent[0] : null
  };
} else {
  // Use the first scheduled content for today
  const content = todayContent[0];

  // Generate a unique ID for this video
  const videoId = `vid-${Date.now()}-${Math.floor(Math.random() * 1000)}`;

  return {
    noContent: false,
    videoId,
    topic: content.topic,
    contentType: content.type,
    keywords: content.keywords,
    targetAudience: content.targetAudience,
    monetizationStrategy: content.monetizationStrategy,
    created: now.toISOString(),
    publishDate: content.publishDate
  };
}

// Merge content plan with monetization products

const contentPlan = $input.item.json;
const products = JSON.parse($node["Get Monetization Products"].json.Body);

// Find relevant products for this content
const relevantProducts = products.filter(product => {
  // Check if product keywords match content keywords
  return product.keywords.some(keyword => 
    contentPlan.keywords.includes(keyword));
});

// Sort by relevance (number of matching keywords)
relevantProducts.sort((a, b) => {
  const aMatches = a.keywords.filter(k => contentPlan.keywords.includes(k)).length;
  const bMatches = b.keywords.filter(k => contentPlan.keywords.includes(k)).length;
  return bMatches - aMatches;
});

// Get top 3 most relevant products
const topProducts = relevantProducts.slice(0, 3);

return {
  ...contentPlan,
  monetizationProducts: topProducts,
  hasProductRecommendations: topProducts.length > 0
};

// Process prompt template

let template;
try {
  template = JSON.parse($node["Get Content Template"].json.Body);
} catch (error) {
  // Default template if specific one not found
  template = {
    scriptPrompt: "Write an engaging YouTube script about [TOPIC]. The target audience is [AUDIENCE]. Include [KEYWORDS].",
    descriptionPrompt: "Write a compelling YouTube description for a video about [TOPIC]. Include relevant keywords: [KEYWORDS].",
    titleOptionsPrompt: "Generate 5 clickable but not clickbait YouTube title options for a video about [TOPIC]. Each title should be SEO optimized and include key terms from [KEYWORDS].",
    thumbnailPrompt: "Create a thumbnail concept for a YouTube video about [TOPIC]."
  };
}

// Get data from previous nodes
const contentData = $input.item.json;

// Replace placeholders in prompts
const scriptPrompt = template.scriptPrompt
  .replace('[TOPIC]', contentData.topic)
  .replace('[AUDIENCE]', contentData.targetAudience)
  .replace('[KEYWORDS]', contentData.keywords.join(', '));

const descriptionPrompt = template.descriptionPrompt
  .replace('[TOPIC]', contentData.topic)
  .replace('[KEYWORDS]', contentData.keywords.join(', '));

const titleOptionsPrompt = template.titleOptionsPrompt
  .replace('[TOPIC]', contentData.topic)
  .replace('[KEYWORDS]', contentData.keywords.join(', '));

const thumbnailPrompt = template.thumbnailPrompt
  .replace('[TOPIC]', contentData.topic);

// If there are product recommendations, include them in the script prompt
let monetizationInstructions = '';
if (contentData.hasProductRecommendations) {
  monetizationInstructions = `\n\nNaturally incorporate these product recommendations in the script:\n`;
  contentData.monetizationProducts.forEach((product, i) => {
    monetizationInstructions += `${i+1}. ${product.name}: ${product.description} (Link: ${product.affiliateLink})\n`;
  });
}

return {
  ...contentData,
  scriptPrompt: scriptPrompt + monetizationInstructions,
  descriptionPrompt,
  titleOptionsPrompt,
  thumbnailPrompt
};

// Combine all LLM generations

const baseData = $input.item.json;
const script = $node["Generate Script"].json.generated_text;
const description = $node["Generate Description"].json.generated_text;
const titleOptionsText = $node["Generate Title Options"].json.generated_text;

// Process title options
let titleOptions = [];
const titleRegex = /\d\.\s*(.*)/g;
let match;
while ((match = titleRegex.exec(titleOptionsText)) !== null) {
  titleOptions.push(match[1]);
}

// Select the first title as default
const title = titleOptions.length > 0 ? titleOptions[0] : `Video about ${baseData.topic}`;

// Generate filenames
const sanitizedTitle = title.replace(/[^a-z0-9]/gi, '-').toLowerCase();
const thumbnailFilename = `${baseData.videoId}-${sanitizedTitle}.png`;
const audioFilename = `${baseData.videoId}-${sanitizedTitle}.wav`;

// Prepare thumbnail prompt - enriched with content context
const thumbnailPrompt = `${baseData.thumbnailPrompt} The video discusses ${baseData.topic} and is aimed at ${baseData.targetAudience}.`;

return {
  ...baseData,
  script,
  description,
  title,
  alternativeTitles: titleOptions.slice(1), // All except the first one
  thumbnailFilename,
  audioFilename,
  thumbnailPrompt
};

// Process thumbnail image from base64

// Get base64 image data
const baseData = $input.item.json;
const base64Image = $node["Generate Thumbnail"].json.images[0];

// Convert to binary
const binaryData = Buffer.from(base64Image, 'base64');

return {
  ...baseData,
  binary: {
    ...($input.item.binary || {}),
    thumbnail: {
      data: binaryData.toString('base64'),
      mimeType: 'image/png',
      fileName: baseData.thumbnailFilename
    }
  }
};

// Voice model selection for Coqui TTS

const baseData = $input.item.json;

// Default to primary recommendation: tacotron2-DDC
let voiceModel = "tts_models/en/ljspeech/tacotron2-DDC";
let useSpeakerId = false;
let speakerId = null;

// Check if content type suggests using multiple voices
// For example, interviews, dialogues, etc. would benefit from multiple voices
if (baseData.contentType && [
  'interview', 
  'dialogue', 
  'conversation', 
  'multi-person',
  'qa',
  'multiple-characters'
].some(type => baseData.contentType.toLowerCase().includes(type))) {
  // Use VCTK model for content that benefits from different voices
  voiceModel = "tts_models/en/vctk/vits";
  useSpeakerId = true;

  // Default to p303 speaker, but can be changed based on content needs
  speakerId = "p303";

  // If script has multiple speakers marked, we'll handle this later
  // but flag it for now
  baseData.hasMultipleSpeakers = baseData.script.includes("Speaker 1:") || 
                                 baseData.script.includes("Person 1:") ||
                                 baseData.script.includes("Host:");
}

// Add voice model information to the data
return {
  ...baseData,
  voiceModel,
  useSpeakerId,
  speakerId,
  ttsProvider: "Coqui TTS"
};

// Process audio file from base64

// Get base64 audio data
const baseData = $input.item.json;
const audioData = $node["Generate Audio"].binary.audio;

return {
  ...baseData,
  binary: {
    ...($input.item.binary || {}),
    thumbnail: $input.item.binary.thumbnail,
    audio: {
      ...audioData,
      fileName: baseData.audioFilename
    }
  }
};

// Prepare CMS content

const baseData = $input.item.json;
const s3Endpoint = $env.S3_ENDPOINT || 'https://minio-service.railway.app';

// Create HTML content
const createHtmlContent = () => {
  // Convert script to HTML
  const scriptHtml = baseData.script
    .split('\n\n')
    .map(para => `<p>${para}</p>`)
    .join('');

  // Add audio player
  const audioUrl = `${s3Endpoint}/youtube-assets/audio/${baseData.audioFilename}`;
  const audioPlayer = `<audio controls style="width:100%;margin:20px 0">
  <source src="${audioUrl}" type="audio/wav">
  Your browser does not support the audio element.
</audio>`;

  // Add featured image reference
  const thumbnailUrl = `${s3Endpoint}/youtube-assets/thumbnails/${baseData.thumbnailFilename}`;

  // If there are product recommendations, add them to the post
  let productSection = '';
  if (baseData.hasProductRecommendations) {
    productSection = `<div class="product-recommendations">
      <h2>Recommended Products</h2>
      <ul>
        ${baseData.monetizationProducts.map(product => 
          `<li><a href="${product.affiliateLink}" target="_blank" rel="nofollow">${product.name}</a> - ${product.description}</li>`
        ).join('')}
      </ul>
    </div>`;
  }

  // Complete HTML content
  return `${scriptHtml}\n${audioPlayer}\n${productSection}`;
};

// Prepare tags from keywords
const prepareTags = () => {
  // Start with content type and topic as tags
  const tags = [
    { name: baseData.contentType },
    { name: baseData.topic.toLowerCase().replace(/\s+/g, '-') }
  ];

  // Add keywords as tags (up to 5)
  baseData.keywords.slice(0, 5).forEach(keyword => {
    tags.push({ name: keyword.toLowerCase().replace(/\s+/g, '-') });
  });

  return tags;
};

return {
  ...baseData,
  htmlContent: createHtmlContent(),
  tags: prepareTags(),
  featuredImage: `${s3Endpoint}/youtube-assets/thumbnails/${baseData.thumbnailFilename}`
};

